<?php/* WWW - PHP micro-frameworkIndex gateway file handlerFile handler is used for every file that is accessed and is not already served by other handlers. This handler serves files such as PDF files or other files that are not usually considered 'web' formats.Author and support: Kristo Vaher - kristo@waher.net*/// Currently known location of the file$resource=str_replace(DIRECTORY_SEPARATOR.DIRECTORY_SEPARATOR,DIRECTORY_SEPARATOR,$_SERVER['DOCUMENT_ROOT'].$_SERVER['REDIRECT_URL']);// Getting information about current resource$fileInfo=pathinfo($resource);// Assigning file information$file=$fileInfo['basename'];// Solving the folder that client is loading resource from$folder=$fileInfo['dirname'].DIRECTORY_SEPARATOR;// If filename includes & symbol, then system assumes it should be dynamically generated$parameters=array_unique(explode('&',$file));// Getting the downloadable file name$file=array_pop($parameters);// The amount of non-filenames in the request$parameterCount=count($parameters);// Currently assumed MIME type$mimeType='';// If more than one parameter is set, it returns 404// 404 is also returned if file does not actually existif($parameterCount>1 || ($parameterCount==1 && !in_array('nocache',$parameters)) || !file_exists($folder.$file)){	// Returning 404 data		require(__DIR__.DIRECTORY_SEPARATOR.'handler.404.php');	die();	}// Finding the proper MIME typeif(extension_loaded('fileinfo')){	// This opens MIME type 'magic' resource for use	$fileInfo=finfo_open(FILEINFO_MIME_TYPE);	if($fileInfo){		// Finding MIME type with magic resource		$mimeType=finfo_file($fileInfo,$folder.$file);		// Resourse is not needed further, so it is closed		finfo_close($fileInfo);	}	}// Last-modified date$lastModified=filemtime($folder.$file);// Assigning MIME type if it was foundif($mimeType && $mimeType!=''){	header('Content-type: '.$mimeType.';');} else {	// Octet stream is a general-use 'download' resource, browser will simply attempt to download the file as a result	header('Content-type: application/octet-stream;');	header('Content-Disposition: attachment; filename='.$file);}// If cache is used, then proper headers will be sentif(!in_array('nocache',$parameters)){	// Default cache timeout of one month, unless timeout is set	if(!isset($config['resource-cache-timeout'])){		$config['resource-cache-timeout']=31536000; // A year	}		// Client is told to cache these results for set duration	header('Cache-Control: public,max-age='.$config['resource-cache-timeout'].',must-revalidate');	header('Expires: '.gmdate('D, d M Y H:i:s',($lastModified+$config['resource-cache-timeout'])).' GMT');	header('Last-modified: '.gmdate('D, d M Y H:i:s',$lastModified).' GMT');} else {	// Client is told to cache these results for set duration	header('Cache-Control: public,max-age=0,must-revalidate');	header('Expires: '.gmdate('D, d M Y H:i:s',$_SERVER['REQUEST_TIME']).' GMT');	header('Last-modified: '.gmdate('D, d M Y H:i:s',$lastModified).' GMT');	}// Pragma header removed should the server happen to set it automaticallyheader_remove('Pragma');// Content length is defined that can speed up website requests, letting client to determine file sizeheader('Content-Length: '.filesize($folder.$file));  // Returning the file to clientreadfile($folder.$file);?>